import React, { Component } from "react";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

export default class ListTodo extends Component {
  state = { todo: [{todo_id: 0, description: ""}] };

  componentDidMount() {
    fetch("http://localhost:5000/todos")
      .then((res) => res.json())
      .then((result) => this.setState({todo: result }, console.log(result)));
  }
  render() {
    return (
      <div>
        {this.state.todo.map((todo) => (
          <Card>
            <CardBody>
              <CardTitle tag="h5">Todo ID: {todo.todo_id}</CardTitle>
              <CardSubtitle tag="h6" className="mb-2 text-muted">
                Todo description:{" "}
              </CardSubtitle>
              <CardText>{todo.description}</CardText>
              <Button color="warning" className="mr-4">
                Edit
              </Button>
              <Button color="danger">Delete</Button>
            </CardBody>
          </Card>
        ))}
      </div>
    );
  }
}

