import React, { Component, useState, useEffect, Fragment } from "react";
import {
  Card,
  Container,
  Row,
  Col,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
  Spinner,
} from "reactstrap";
import EditTodo from "./EditTodo";

const ListTodo = () => {
  const [todo, setTodo] = useState([]);

  const getTodos = () => {
    fetch("http://localhost:5000/todos")
      .then((res) => res.json())
      .then((result) => setTodo({ result }, console.log(result)));
  };

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <Fragment>
      {todo.map(todo=> <li>{todo.description}</li>)}
    </Fragment>
  );
};

export default ListTodo;

