      );
  }
  deleteTodo = async (id) => {
    const { todo } = this.state;
    try {
      const deleteTodo = await fetch(`http://localhost:5000/todos/${id}`, {
        method: "DELETE",
      });

      this.setState({ todo: todo.filter((todo) => todo.todo_id !== id) });
    } catch (err) {
      console.error(err.message);
    }
  };
  updateDescription = (childData) => {
