import React, { Component, useState } from "react";
import { Button } from "reactstrap";
import { Modal } from "react-bootstrap";

function MyVerticallyCenteredModal(props) {
  const putMethod = async (id) => {
    const { todo } = this.state;
    try {
      const update = await fetch(`http://localhost:5000/todos/${id}`, {
        method: "PUT",
      });
    } catch (error) {
      console.error(error);
    }
  };

  const updateTodo = () => {
    fetch("http://localhost:5000/todos")
      .then((res) => res.json())
      .then((result) =>
        this.setState({ todo: result, isLoading: false }, console.log(result))
      );
  };
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">Edit </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form className="m-4">
          <input type="text" className="form-control" />
        </form>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={(this.props.updateDescription(), props.onHide)}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
}

function EditTodo() {
  const [modalShow, setModalShow] = React.useState(false);

  return (
    <>
      <Button color="warning" onClick={() => setModalShow(true)}>
        Edit{" "}
      </Button>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}

export default EditTodo;
