import React, { useState, useEffect, Fragment } from "react";
import {
  Card,
  CardText,
  Container,
  Row,
  CardBody,
  CardTitle,
  Button,
  Spinner,
} from "reactstrap";
import EditTodo from "./EditTodo";
import GridLayout from 'react-grid-layout';


const ListTodos = () => {
  class MyFirstGrid extends React.Component {
    render() {
   
      // layout is an array of objects, see the demo for more complete usage
      const layout = [
        {i: 'a', x: 0, y: 0, w: 1, h: 2, static: true},
        {i: 'b', x: 1, y: 0, w: 3, h: 2, minW: 2, maxW: 4},
        {i: 'c', x: 4, y: 0, w: 1, h: 2}
      ];
      return (
        <GridLayout className="layout" layout={layout} cols={12} rowHeight={30} width={1200}>
          <div key="a">a</div>
          <div key="b">b</div>
          <div key="c">c</div>
        </GridLayout>
      )
    }
  }
  const [todo, setTodos] = useState([]);
  const [isLoading, setLoading] = useState(true);

  const deleteTodo = async (id) => {
    try {
      const respone = await fetch(`http://localhost:5000/todos/${id}`, {
        method: "DELETE",
      });
      setTodos(todo.filter((todo) => todo.todo_id !== id));
    } catch (error) {
      console.error(error);
    }
  };
  const getTodos = async () => {
    try {
      const response = await fetch("http://localhost:5000/todos");
      const jsonData = await response.json();
      setTodos(jsonData);
      setLoading(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  useEffect(() => {
    getTodos();
  }, []);
  if (isLoading) {
    return (
      <Container>
        <Row className="justify-content-center">
          {" "}
          <Spinner color="primary" />
        </Row>
      </Container>
    );
  }
  return (
    <Fragment>
      {todo.map((todo) => (
        <Card key={todo.todo_id}>
          <CardBody>
            <CardTitle tag="h5">Description</CardTitle>{" "}
            <CardText>{todo.description} </CardText>
            <EditTodo todos={todo} />
            <Button color="danger" onClick={() => deleteTodo(todo.todo_id)}>
              Delete
            </Button>
          </CardBody>
        </Card>
      )).reverse()}
    </Fragment>
  );
};

export default ListTodos;
